import React,{useState} from 'react';


const App = () => {
    
    const purple = "#8e44ad";
    const [bg, setBg] = useState(purple);
    const [name, setName]= useState('click me');
    const bgChange = () => {
        //console.log("clicked");
        let newBg = '#34495b';
        setBg(newBg);
        setName("Ouch! ☹️")
    }; 

    return(
        <>
            <div style={ { backgroundColor : bg } }>
                <button onClick = {bgChange}> {name} </button>
            </div>
        </>
    );
}


export default App;
