import React from 'react';

const App = () => {

    let newTime = new Date().toLocaleTimeString();
    const state = useState();
    const [ctime, setCtime] = useState(newTime);

    const UpdateTime = () => {
        newTime = new Date().toLocaleTimeString();
        setCtime(newTime);
    }
    return(
        <>
        <h1> {ctime} </h1>
        <button onClick={UpdateTime}> Get time </button>
    </>
    );
}

export default App;
