import React,{useState} from 'react';

const App = () => {
    const [fullName, setfullName] = useState({
        fname : "",
        lname : "",
        email : "",
        phone : "",
    });

    const inputEvent = (event) => {
        console.log(event.target.value);
        console.log(event.target.name);
        
        const { name, value } = event.target;

        setfullName ((preValue) => {
            console.log(preValue);

            return{
                ...preValue,
                [name] : value,
            };
            

            
           /*if(name === "fName"){
               return{
                fname : value,
                lname : preValue.lname,
                email : preValue.email,
                 phone : preValue.phone,
               }; 
            } else if(name === "lName"){
                return{
                 fname : preValue.fname,
                 lname : value,
                 email : preValue.email,
                 phone : preValue.phone,

                }; 
             }else if(name === "email"){
                return{
                 fname : preValue.fname,
                 lname : preValue.value,
                 email : value,
                 phone : preValue.phone,
                }; 
             }else if(name === "phone"){
                return{
                 fname : preValue.fname,
                 lname : preValue.value,
                 email : preValue.email,
                 phone : value,
                }; 
             }*/
        
        });
    };
   
    const onSubmit = (event) =>{
        event.preventDefault();
        alert('form submitted');
    }

    return(
        <>
            <div className="main_div">
            <form onSubmit={onSubmit}>
                <div>
                    <h1> Hello {fullName.fname} {fullName.lname} </h1>
                    <p>{fullName.email}</p>
                    <p>{fullName.phone}</p>
                    <input type="text" 
                            placeholder="Enter Your Name" 
                                name='fName' onChange={inputEvent} value={fullName.fname} 
                    />
                    <br/>
                    <input type="text" 
                            placeholder="Enter Your Last Name" 
                                name='lName' onChange={inputEvent} value={fullName.lname} 
                    />
                     <input type="email" 
                            placeholder="Enter Your email" 
                                name='email' onChange={inputEvent} value={fullName.email} 
                    />
                     <input type="number" 
                                placeholder="Enter Your phone" 
                                    name='phone' onChange={inputEvent} value={fullName.phone} 
                    />
                    <br/>
                    <button type="submit"  > Click Me </button>
                </div>
            </form>
            </div>
        </>
    );
};

export default App;
