import React, {useState} from "react";
import Button from "@material-ui/core/Button";
import AddIcon from '@material-ui/icons/Add';

const CreateNote = (props) => {

    const [note, setNote] = useState({
        title:"",
        content:"",
    });

    const InputEvent = (event) =>{

        const { name, value } = event.target;

        setNote((preValue)=>{
            return{
                ...prevData,
                [name]: value,
            };
        });
        console.log(note);
    };

    const addEvent=()=>{
        props.passNote(note);
    };

    return (
        <>
            <div className="main_note">
                <form>
                    <input type="text" name="title" value={note.content} onChange={InputEvent} placeholder="Title" autoComplete="off" />
                    <textarea rows="" column="" name="content" value={note.content} onChange={InputEvent} placeholder="write a note..."></textarea>
                    <Button onClick={addEvent}>
                        <AddIcon className="plus_sign"/>
                    </Button>
                </form>
            </div>
        </>
    );
};

export default CreateNote;
