import React from 'react';


const App = () => {
    
    const bgChange = () => {
        //console.log("clicked");
        const purple = "#8e44ad";
        const [bg, setBg] = useState(purple);
    }; 

    return(
        <>
            <div style={ { backgroundColor : bg } }>
                <button onClick = {bgChange}> Click Me </button>
            </div>
        </>
    );
}


export default App;
